import React from 'react';

export default function Home() {
  return (
    <div className="bg-cover bg-center h-[-500px] w-full flex items-center justify-center">
      <div className="grid grid-cols-2 gap-4 p-4 bg-white shadow-lg rounded-lg">
        {/* Ô sản phẩm đầu tiên */}
        <div className="flex flex-col justify-between p-4 border rounded-lg">
          <div className="bg-gray-300 h-24 mb-4 rounded-md">*Ảnh sản phẩm</div>
          <div className="text-lg font-semibold">*Tên sản phẩm</div>
          <div className="text-orange-500 text-right font-bold">100,000.00₫</div>
        </div>

        {/* Ô sản phẩm tích cực */}
        <div className="flex flex-col justify-between p-4 border rounded-lg">
          <div className="bg-gray-300 h-24 mb-4 rounded-md">*Ảnh sản phẩm</div>
          <div className="flex justify-between items-center">
            <span className="text-yellow-500 font-semibold">⭐ 5</span>
            <span className="text-gray-500">1,000 lượt</span>
          </div>
        </div>

        {/* Các ô trống còn lại */}
        {Array(6).fill(0).map((_, index) => (
          <div key={index} className="flex flex-col justify-between p-4 border rounded-lg h-32"></div>
        ))}
      </div>
    </div>
  );
}
