import React from 'react';

export default function Cart() {
  return (
    <div className="flex items-center justify-center">
      <div className="p-4 bg-gray-100 bg-opacity-30 w-[1800px] h-[750px] mt-4 rounded-3xl">
        {/* Header Section */}
        <div className="flex justify-between items-center bg-[#86B8D4] text-white px-6 py-4 rounded-t-lg">
          <div className="bg-white rounded-lg px-4 py-2 text-lg font-semibold text-center">Tổng tiền</div>
          <button className="bg-[#458FFF] px-4 py-2 rounded-lg">Mua</button>
        </div>

        {/* Cart Items */}
        <div className="p-6">
          {Array(3).fill(0).map((_, index) => (
            <div key={index} className="flex items-center justify-between border-b border-gray-200 py-4">
              <div className="flex items-center">
                {/* Product Image Placeholder */}
                <div className="w-16 h-16 bg-gray-300 rounded-lg mr-4 flex items-center justify-center">*Ảnh sản phẩm</div>
                <div>
                  <div className="font-semibold">Tên sản phẩm</div>
                  <div className="text-blue-500">Tên cửa hàng</div>
                </div>
              </div>

              {/* Quantity and Price */}
              <div className="text-orange-500 font-semibold">x1</div>
              <div className="text-orange-500 font-semibold">100,000.00đ</div>

              {/* Delete Button */}
              <button className="text-red-500">
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                </svg>
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
